		public Color GetColor(Color newColor)
		{
			int r = this.color.R - (255 - newColor.R);
			int g = this.color.G - (255 - newColor.G);
			int b = this.color.B - (255 - newColor.B);
			int a = this.color.A - (255 - newColor.A);
			if (r < 0)
			{
				r = 0;
			}
			if (r > 255)
			{
				r = 255;
			}
			if (g < 0)
			{
				g = 0;
			}
			if (g > 255)
			{
				g = 255;
			}
			if (b < 0)
			{
				b = 0;
			}
			if (b > 255)
			{
				b = 255;
			}
			if (a < 0)
			{
				a = 0;
			}
			if (a > 255)
			{
				a = 255;
			}
			return new Color(r, g, b, a);
		}

