				if (itemWidth >= 52)
				{
					single = 24f;
				}
				if (itemWidth >= 64)
				{
					single = 28f;
				}
				if (itemWidth >= 92)
				{
					single = 38f;
				}
				zero.X = base.Center.X + ((float)itemWidth * 0.5f - single) * (float)aimDir;
				zero.Y = this.position.Y + 24f;
