		public static bool MechSpawn(float x, float y, int type)
		{
			int num = 0;
			int num1 = 0;
			int num2 = 0;
			for (int i = 0; i < 200; i++)
			{
				if (Main.npc[i].active && Main.npc[i].type == type)
				{
					num++;
					Vector2 vector2 = new Vector2(x, y);
					float single = Main.npc[i].position.X - vector2.X;
					float single1 = Main.npc[i].position.Y - vector2.Y;
					float single2 = (float)Math.Sqrt((double)(single * single + single1 * single1));
					if (single2 < 200f)
					{
						num1++;
					}
					if (single2 < 600f)
					{
						num2++;
					}
				}
			}
			if (num1 < 3 && num2 < 6 && num < 10)
			{
				return ServerApi.Hooks.InvokeGameStatueSpawn(num1, num2, num, (int)(x / 16), (int)(y / 16), type, true);
			}
			return false;
		}

